PackageOracle.source:
create or replace PACKAGE restaurantmanagers_pkg AS
  PROCEDURE insert_manager(p_manager_id IN INTEGER, p_nume IN VARCHAR2, p_phone IN VARCHAR2, p_email IN VARCHAR2, p_restaurant_id IN INTEGER);
  PROCEDURE update_manager(p_manager_id IN INTEGER, p_nume IN VARCHAR2, p_phone IN VARCHAR2, p_email IN VARCHAR2, p_restaurant_id IN INTEGER);
  PROCEDURE delete_manager(p_manager_id IN INTEGER);
END restaurantmanagers_pkg;

body.source:
create or replace PACKAGE BODY restaurantmanagers_pkg AS
  PROCEDURE insert_manager(p_manager_id IN INTEGER, p_nume IN VARCHAR2, p_phone IN VARCHAR2, p_email IN VARCHAR2, p_restaurant_id IN INTEGER) IS
  BEGIN
    INSERT INTO restaurantmanagers (manager_id, nume, phone, email, restaurant_id) VALUES (p_manager_id, p_nume, p_phone, p_email, p_restaurant_id);
  END insert_manager;

  PROCEDURE update_manager(p_manager_id IN INTEGER, p_nume IN VARCHAR2, p_phone IN VARCHAR2, p_email IN VARCHAR2, p_restaurant_id IN INTEGER) IS
  BEGIN
    UPDATE restaurantmanagers SET nume = p_nume, phone = p_phone, email = p_email, restaurant_id = p_restaurant_id WHERE manager_id = p_manager_id;
  END update_manager;

  PROCEDURE delete_manager(p_manager_id IN INTEGER) IS
  BEGIN
    DELETE FROM restaurantmanagers WHERE manager_id = p_manager_id;
  END delete_manager;
END restaurantmanagers_pkg;
