PackageOracle.source:
create or replace PACKAGE tabless_pkg AS
  PROCEDURE insert_table(p_table_id IN INTEGER, p_numar IN INTEGER, p_seating_capacity IN INTEGER, p_restaurant_id IN INTEGER);
  PROCEDURE update_table(p_table_id IN INTEGER, p_numar IN INTEGER, p_seating_capacity IN INTEGER, p_restaurant_id IN INTEGER);
  PROCEDURE delete_table(p_table_id IN INTEGER);
END tabless_pkg;

body.source:
create or replace PACKAGE BODY tabless_pkg AS
  PROCEDURE insert_table(p_table_id IN INTEGER, p_numar IN INTEGER, p_seating_capacity IN INTEGER, p_restaurant_id IN INTEGER) IS
  BEGIN
    INSERT INTO tabless (table_id, numar, seating_capacity, restaurant_id) VALUES (p_table_id, p_numar, p_seating_capacity, p_restaurant_id);
  END insert_table;

  PROCEDURE update_table(p_table_id IN INTEGER, p_numar IN INTEGER, p_seating_capacity IN INTEGER, p_restaurant_id IN INTEGER) IS
  BEGIN
    UPDATE tabless SET numar = p_numar, seating_capacity = p_seating_capacity, restaurant_id = p_restaurant_id WHERE table_id = p_table_id;
  END update_table;

  PROCEDURE delete_table(p_table_id IN INTEGER) IS
  BEGIN
    DELETE FROM tabless WHERE table_id = p_table_id;
  END delete_table;
END tabless_pkg;
