PackageOracle.source:
create or replace PACKAGE customers_pkg AS
  PROCEDURE insert_customer(p_customer_id IN INTEGER, p_nume IN VARCHAR2, p_phone IN VARCHAR2, p_email IN VARCHAR2);
  PROCEDURE update_customer(p_customer_id IN INTEGER, p_nume IN VARCHAR2, p_phone IN VARCHAR2, p_email IN VARCHAR2);
  PROCEDURE delete_customer(p_customer_id IN INTEGER);
END customers_pkg;

body.source:
create or replace PACKAGE BODY customers_pkg AS
  PROCEDURE insert_customer(p_customer_id IN INTEGER, p_nume IN VARCHAR2, p_phone IN VARCHAR2, p_email IN VARCHAR2) IS
  BEGIN
    INSERT INTO customers (customer_id, nume, phone, email) VALUES (p_customer_id, p_nume, p_phone, p_email);
  END insert_customer;

  PROCEDURE update_customer(p_customer_id IN INTEGER, p_nume IN VARCHAR2, p_phone IN VARCHAR2, p_email IN VARCHAR2) IS
  BEGIN
    UPDATE customers SET nume = p_nume, phone = p_phone, email = p_email WHERE customer_id = p_customer_id;
  END update_customer;

  PROCEDURE delete_customer(p_customer_id IN INTEGER) IS
  BEGIN
    DELETE FROM customers WHERE customer_id = p_customer_id;
  END delete_customer;
END customers_pkg;
