PackageOracle.source:
create or replace PACKAGE restaurants_pkg AS
  PROCEDURE insert_restaurant(p_restaurant_id IN INTEGER, p_nume IN VARCHAR2, p_location IN VARCHAR2, p_capacity IN INTEGER);
  PROCEDURE update_restaurant(p_restaurant_id IN INTEGER, p_nume IN VARCHAR2, p_location IN VARCHAR2, p_capacity IN INTEGER);
  PROCEDURE delete_restaurant(p_restaurant_id IN INTEGER);
END restaurants_pkg;

body.source:
create or replace NONEDITIONABLE PACKAGE BODY restaurants_pkg AS
  PROCEDURE insert_restaurant(p_restaurant_id IN INTEGER, p_nume IN VARCHAR2, p_location IN VARCHAR2, p_capacity IN INTEGER) IS
  BEGIN
    INSERT INTO restaurants (restaurant_id, nume, location, capacity) VALUES (p_restaurant_id, p_nume, p_location, p_capacity);
  END insert_restaurant;

  PROCEDURE update_restaurant(p_restaurant_id IN INTEGER, p_nume IN VARCHAR2, p_location IN VARCHAR2, p_capacity IN INTEGER) IS
  BEGIN
    UPDATE restaurants SET nume = p_nume, location = p_location, capacity = p_capacity WHERE restaurant_id = p_restaurant_id;
  END update_restaurant;

  PROCEDURE delete_restaurant(p_restaurant_id IN INTEGER) IS
  BEGIN
    DELETE FROM restaurants WHERE restaurant_id = p_restaurant_id;
  END delete_restaurant;
END restaurants_pkg;
